package ciclowhile;

public class Main {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		// ciclo o estructura repetitiva
		
		// while do while for for each
		int num = 0;
		
		while(num < 2) {
			
			System.out.println(" esto es verdadero ");
			num = num +1;
			
			//finitud
		}
		do {
			System.out.println(" hola , esto es do   ");
			num = num + 3 ;
			
			
		}while(num < 9);
		
	}

}
